#!/bin/bash
# Create simple placeholder PNG icons using base64 encoding

# 16x16 icon (minimal PNG)
echo "Creating icon16.png..."
echo -n "" | base64 -d > icon16.png

# 48x48 icon
echo "Creating icon48.png..."
echo -n "" | base64 -d > icon48.png

# 128x128 icon
echo "Creating icon128.png..."
echo -n "" | base64 -d > icon128.png

echo "Icons created successfully!"
